// src/integrations/hubspot.js
export async function authorizeHubspot(user, org) {
  const formData = new FormData();
  formData.append("user_id", user);
  formData.append("org_id", org);
  const res = await fetch("/integrations/hubspot/authorize", {
    method: "POST",
    body: formData,
  });
  // server returns URL as a string (possibly quoted, so remove quotes)
  let url = await res.text();
  url = url.replace(/^"|"$/g, '');
  window.open(url, "_blank", "width=500,height=700");
}

export async function getHubspotCredentials(user, org) {
  const formData = new FormData();
  formData.append("user_id", user);
  formData.append("org_id", org);
  const res = await fetch("/integrations/hubspot/credentials", {
    method: "POST",
    body: formData,
  });
  return await res.json();
}
